import pic from './logo.svg';
import { CartContext } from './CartContext';
import { useContext ,useEffect, useState } from 'react'
function Cart() {
   const {cart}= useContext(CartContext);
   const {products, setProduct}= useState([]);

  console.log(Object.keys(cart.items));
  
   useEffect(()=>{
       if(!cart.items){
           return;
       }
       fetch('/api/products',{
            method: 'POST',
            headers:{"Content-Type": "application/json"},
            body:JSON.stringify({ids:Object.keys(cart.items)})
       })
       .then((res)=>{res.json()})
       .then((data)=>{
           console.log(data);
           setProduct(data);
       })
   },[cart]);
    return ( 
        <div>
            <table>
                <tr>
                    <th>Image</th>
                    <th>Product name</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Action</th>
                </tr>
                {products.map((result)=>(
                <tr key={result._id}>
                    
                    <td><img src={pic} alt='..' /></td>
                    <td>{result.name}</td>
                    <td></td>
                    <td></td>
                    <td></td>

                </tr>
                ))};
            </table>
       </div>
     );
}

export default Cart;
