import { useEffect,useState } from "react";
import { useParams } from "react-router-dom";

function Singlepage() {
    const [product ,setProduct]=useState();
    const {id}= useParams();

    useEffect(()=>{
        fetch(`/api/${id}`)
        .then((res)=>{return res.json()})
        .then((data)=>{
            console.log(data);
            setProduct(data);
        })
    },[])
    return ( 
        <h1>{product.name}</h1>
     );
}

export default Singlepage;
