   const {products, setProduct}= useState([]);

  console.log(Object.keys(cart.items))
   useEffect(()=>{
       if(!cart.items){
           return;
       }
       fetch('/api/products',{
            method: 'POST',
            headers:{"Content-Type": "application/json"},
            body:JSON.stringify({ids:Object.keys(cart.items)})
       })
       .then((res)=>{res.json()})
       .then((data)=>{
           console.log(data);
           //setProduct(data);
       })
   },[cart]);
    return ( 
        <div>
            <h1>cart</h1>
       </div>
     );
}

